project.property:
function makeLoaders(fmt, priority) {
	if (priority == null) priority = ["*"];

	// Priority numbering.
	var PRI = {};
	for (var i=0;i<priority.length;i++) {
		PRI[priority[i]] = i;
	}

	// make file name list.
	var names = [];
	var ite = fileset.iterator();
	while (ite.hasNext()) {
		var reg = new RegExp(prefix);
		var fname = (""+ite.next()).replace(reg,"");
		var pri = PRI[fname.match(/[^/]*$/)];
		if (pri == null) pri = PRI["*"];
		names.push({pri:pri, fname:fname});
	}

	// sort by priority.
	names.sort(function(a,b){
		if (a.pri != b.pri) return a.pri-b.pri;
		return a.fname>b.fname?1:-1;
	});

	// make loader string.
	var loaders = "";
	for (var i=0;i<names.length;i++) {
		//task.log(names[i].pri+":"+names[i].fname);
		loaders += fmt.replace("${fname}",names[i].fname);
	}
	return loaders;
}

