eval(""+task.project.getProperty("make.lodaer.js"));

var PRIORITY = [
	"Lang.js",
	"Font.js", "Color.js",

	"Coor.js",	"CoorDiag.js", 
	"Elem.js", "Item.js",
	"Items.js",	"Handle.js",
	"Rectangle.js",
	"Group.js",	"SelectGroup.js",

	"Action.js",
	"*",
	"Editor.js"
];
var fmt = "<script src='${fname}'></script>\n";
var loaders = makeLoaders(fmt,PRIORITY);
task.project.setProperty("jsloaders",loaders);





